option.value:
#include <stdio.h>
#include <stdlib.h>
#include <stdbool.h>

static int cmp_int_asc(const void *pa, const void *pb) {
    int a = *(const int *)pa;
    int b = *(const int *)pb;
    if (a < b) return -1;
    if (a > b) return 1;
    return 0;
}

int main(void) {
    int t;
    if (scanf("%d", &t) != 1) return 0;
    while (t--) {
        int n;
        if (scanf("%d", &n) != 1) return 0;
        int *a = (int *)malloc((size_t)n * sizeof(int));
        if (!a) return 0;
        bool has_even = false, has_odd = false;
        for (int i = 0; i < n; ++i) {
            scanf("%d", &a[i]);
            if ((a[i] & 1) == 0) has_even = true; else has_odd = true;
        }
        if (has_even && has_odd) {
            qsort(a, (size_t)n, sizeof(int), cmp_int_asc);
        }
        for (int i = 0; i < n; ++i) {
            if (i) putchar(' ');
            printf("%d", a[i]);
        }
        putchar('\n');
        free(a);
    }
    return 0;
}
